mapper.select:
select itemCode,ifnull(itemName,'空') itemName,cnt count from
(
select concat(t.hospital,'-',t.dept_code) itemCode,
(select dept_name from ods.dept_dict a where a.hospital=#{hspId} and dept_code=t.dept_code) itemName,
sum(inp_count) cnt
from ods.ods_WORK_LOAD t force index(WORK_LOAD_sumindex)
where t.hospital= #{hspId}
and t.visit_date >= #{startDate}
and t.visit_date < #{endDate}
group by concat(t.hospital,'-',t.dept_code)
having cnt>0
) b
order by cnt desc

select itemCode,ifnull(itemName,'空') itemName,cnt count from
(	select t.doctor itemCode,
(select name from dc.staff_dict a where a.hospital=#{hspId} and a.emp_no=t.doctor) itemName,
count(*) cnt
from dc.clinic_master t
where t.hospital=#{hspId} and t.dept_code=#{deptCode}
and t.visit_date >= #{startDate}
and t.visit_date < #{endDate}
group by t.doctor
) b
order by cnt desc

select '000' id,"平均值" itemName, IFNULL(ROUND(AVG(t.countNew)),0)countNew, IFNULL(ROUND(AVG(t.countOld)),0)countOld from( select hsp.id,hsp.item_Name itemName, (select sum(inp_count) from ods.ods_WORK_LOAD t force index(WORK_LOAD_sumindex) where t.visit_date>= #{startDate} and t.visit_date <= #{endDate} and hospital=hsp.id) countNew, (select sum(inp_count) from ods.ods_WORK_LOAD t force index(WORK_LOAD_sumindex) where t.visit_date>= concat(cast(date_format(#{startDate},'%Y') - 1 as CHAR),date_format(#{startDate},'-%m-%d'),' 00:00:00.0') and t.visit_date <= concat(cast(date_format(#{endDate},'%Y') - 1 as CHAR),date_format(#{endDate},'-%m-%d'),' 23:59:59.0') and hospital=hsp.id) countOld from hsp.hsp_config_baseinfo hsp where LENGTH(hsp.id)=10 and hsp.comm_config_unitgrade_id=(select comm_config_unitgrade_id from hsp.hsp_config_baseinfo where item_Name=#{hspName}) ) t
union all
select hsp.id,hsp.item_Name itemName, (select sum(inp_count) thisYear from ods.ods_WORK_LOAD t force index(WORK_LOAD_sumindex) where t.visit_date>= #{startDate} and t.visit_date <= #{endDate} and hospital=hsp.id) countNew, (select sum(inp_count) from ods.ods_WORK_LOAD t force index(WORK_LOAD_sumindex) where t.visit_date>= concat(cast(date_format(#{startDate},'%Y') - 1 as CHAR),date_format(#{startDate},'-%m-%d'),' 00:00:00.0') and t.visit_date <= concat(cast(date_format(#{endDate},'%Y') - 1 as CHAR),date_format(#{endDate},'-%m-%d'),' 23:59:59.0') and hospital=hsp.id) countOld from hsp.hsp_config_baseinfo hsp where LENGTH(hsp.id)=10 and hsp.comm_config_unitgrade_id=(select comm_config_unitgrade_id from hsp.hsp_config_baseinfo where item_Name=#{hspName});
