mapper.select:
select t1.itemCode itemCode,t1.itemName itemName,ifnull(t2.cnt,0)count from
 (
 	select sd.hospital||'-'||sd.dept_code||'-'||sd.emp_no as itemCode,sd.name as itemName
 	from dc.staff_dict sd
 	where sd.hospital=#{hspId} and sd.dept_code=#{deptCode}
 ) t1
 left join
 (
 	select t.hospital||'-'||t.dept_code||'-'||t.doctor itemCode,count(*)cnt
 	from dc.bed_record t
 	where t.hospital=#{hspId} and t.dept_code=#{deptCode}
	and t.Create_date >= #{startDate}
	and t.Create_date<= #{endDate}
	and t.Severe_bed_state='0'
 	group by t.hospital||'-'||t.dept_code||'-'||t.doctor
  ) t2
  on t1.itemCode=t2.itemCode
  order by ifnull(t2.cnt,0) desc

select '000' id,"平均值" itemName, IFNULL(ROUND(AVG(t.countNew)),0)countNew, IFNULL(ROUND(AVG(t.countOld)),0)countOld from( SELECT hsp.id,hsp.item_Name itemName, (SELECT SUM(Bed_icu) thisYearCount FROM ods.ODS_HOSPITAL_RESOURCE_NEW t WHERE DATE_FORMAT(Year,'%Y') = DATE_FORMAT(#{endDate},'%Y') AND t.hospital=hsp.id) countNew, (SELECT SUM(Bed_icu) agoYearCount FROM ods.ODS_HOSPITAL_RESOURCE_NEW t WHERE DATE_FORMAT(Year,'%Y') = (DATE_FORMAT(#{endDate},'%Y')- 1) AND t.hospital=hsp.id) countOld FROM hsp.hsp_config_baseinfo hsp WHERE LENGTH(hsp.id)=10 and hsp.comm_config_unitgrade_id=(select comm_config_unitgrade_id from hsp.hsp_config_baseinfo where item_Name=#{hspName}) ) t
union all
SELECT hsp.id,hsp.item_Name itemName, (SELECT SUM(Bed_icu) thisYearCount FROM ods.ODS_HOSPITAL_RESOURCE_NEW t WHERE DATE_FORMAT(Year,'%Y') = DATE_FORMAT(#{endDate},'%Y') AND t.hospital=hsp.id) countNew, (SELECT SUM(Bed_icu) agoYearCount FROM ods.ODS_HOSPITAL_RESOURCE_NEW t WHERE DATE_FORMAT(Year,'%Y') = (DATE_FORMAT(#{endDate},'%Y')- 1) AND t.hospital=hsp.id) countOld FROM hsp.hsp_config_baseinfo hsp WHERE LENGTH(hsp.id)=10 and hsp.comm_config_unitgrade_id=(select comm_config_unitgrade_id from hsp.hsp_config_baseinfo where item_Name=#{hspName});
