mapper.select:
 select t1.item_code itemCode,t1.item_name itemName, 			
			round(case when IFNULL(t2.cnt2,0)=0 then 0 else IFNULL(t3.cnt3,0)/t2.cnt2 * 1.00 end,2) count
 from hsp.hsp_config_baseinfo  t1
 left join
(select t.hospital hospital, sum(Outp_Count) cnt2
		from ods.ods_WORK_LOAD t force index(WORK_LOAD_sumindex)
		where t.hospital  in 
		and t.visit_date >= #{startDate}
		and t.visit_date< #{endDate} 
		group by t.hospital	
	) t2 on t1.item_code = t2.hospital
left join
	(select t.hospital hospital, sum(Outp_drug) cnt3 
	 from ods.ods_HOSPITAL_INCOME t force index(HOSPITAL_INCOME_sumindex)
	 where t.hospital in 
	 and t.visit_date >= #{startDate}
	 and t.visit_date< #{endDate} 
	 and t.dept_code !='CW001' and t.dept_code !='tj001'
	 group by t.hospital	
	) t3 on t1.item_code = t3.hospital
where t1.item_code in 
and length(t1.item_code)=10
and t3.cnt3>0
order by count desc;

				     select t1.itemCode itemCode,t1.itemName itemName, 			
 round(case when IFNULL(t2.cnt2,0)=0 then 0 else IFNULL(t3.cnt3,0)/t2.cnt2 * 1.00 end,2) count
          
		from
		  (
		   	select hcb.id as itemCode, hcb.item_name as itemName, hcb.seq_no as seqNo
			from hsp.hsp_config_baseinfo hcb
			where hcb.parent_item_code = #{parentId}
		and hcb.id in 
		and length(hcb.item_code)=10
		  ) t1
		  left join
		  (
		    select t.hospital hospital, sum(Outp_Count) cnt2
		    from ods.ods_WORK_LOAD t force index(WORK_LOAD_sumindex)
		    where t.hospital in (select id from hsp.hsp_config_baseinfo where parent_item_code = #{parentId} and id in  and length(item_code)=10)
			and t.visit_date >= #{startDate}
			and t.visit_date< #{endDate} 
			group by t.hospital	
		  ) t2
		  on t1.itemCode = t2.hospital
		  
		   left join
		  (
		    select t.hospital hospital, sum(Outp_drug) cnt3 
		    from ods.ods_HOSPITAL_INCOME t force index(HOSPITAL_INCOME_sumindex)
		    where t.hospital in (select id from hsp.hsp_config_baseinfo where parent_item_code = #{parentId} and id in  and length(item_code)=10)
		   and t.visit_date >= #{startDate}
			and t.visit_date< #{endDate} 
		
			group by t.hospital	
		  ) t3
		  on t1.itemCode = t3.hospital
		  	where t3.cnt3>0
		  
		order by t1.seqNo

		     select t1.itemCode itemCode,t1.itemName itemName,
        round(case when IFNULL(t2.cnt2,0)=0 then 0 else IFNULL(t3.cnt3,0)/t2.cnt2 * 1.00 end,2) count 
        
        from
		 (
		 	select concat(b.hospital,'-',b.dept_code) itemCode,b.dept_name itemName
		 	from ods.dept_dict b
		 	where b.hospital=#{hspId}
		 ) t1
		 
		 left join
		 (
		 	select concat(t.hospital,'-',t.dept_code) itemCode,sum(Outp_Count) cnt2 
		 	from ods.ods_WORK_LOAD t force index(WORK_LOAD_sumindex) 
		 	where t.hospital= #{hspId}
			and t.visit_date >= #{startDate}
			and t.visit_date< #{endDate} 
		 	group by concat(t.hospital,'-',t.dept_code)
		 ) t2
		  on t1.itemCode=t2.itemCode
		 left join
		 (
		 	select concat(t.hospital,'-',t.dept_code) itemCode,sum(Outp_drug) cnt3 
		 	from ods.ods_HOSPITAL_INCOME t force index(HOSPITAL_INCOME_sumindex) 
		 	where t.hospital= #{hspId}
		   and t.visit_date >= #{startDate}
			and t.visit_date< #{endDate} 
		 	group by concat(t.hospital,'-',t.dept_code)
		 ) t3
		 
		 
		 on t1.itemCode=t3.itemCode
		 	where t3.cnt3>0
		 order by count desc

		select t1.itemCode itemCode,t1.itemName itemName,ifnull(t2.cnt,0)count from
		 (
		 	select sd.hospital||'-'||sd.dept_code||'-'||sd.emp_no as itemCode,sd.name as itemName
		 	from dc.staff_dict sd
		 	where sd.hospital=#{hspId} and sd.dept_code=#{deptCode}
		 ) t1
		 left join
		 (
		      select t.hospital hospital,round(case when IFNULL(t1.count,0)=0 then 0.00 else IFNULL(t.count,0)/IFNULL(t1.count,0)*1.00 end,2) cnt from
              (
                SELECT hospital||'-'||dept_code||'-'||t.doctor,
                sum(Costs) count
                FROM dc.outp_bill_items
                WHERE 1=1
                and (Item_class='A' or Item_class='B')
                AND substr(Hospital,1,LENGTH ('${hspId}'))= #{hspId}
                AND Visit_date >= #{startDate}
                AND Visit_date <= #{endDate}
                GROUP BY hospital||'-'||dept_code||'-'||t.doctor) t
                right join
                (SELECT hospital||'-'||dept_code||'-'||t.doctor,
                    count(*) count
                FROM dc.clinic_master
                WHERE 1=1
                AND substr(Hospital,1,LENGTH ('${hspId}'))= #{hspId}
                AND Visit_date >= #{startDate}
                AND Visit_date <= #{endDate}
                GROUP BY hospital||'-'||dept_code||'-'||t.doctor
                ) t1 on t.hospital=t1.hospital
		  ) t2
		  on t1.itemCode=t2.itemCode
		  order by ifnull(t2.cnt,0) desc

		
		    
    SELECT id,itemname,ROUND(CASE WHEN IFNULL(this_OutpCounts,0)=0 THEN 0 ELSE this_OutpDrugs/this_OutpCounts END ,2) countNew,
	ROUND(CASE WHEN IFNULL(last_OutpCounts,0)=0 THEN 0 ELSE last_OutpDrugs/last_OutpCounts END ,2) countOld
FROM(
SELECT 
  hsp.id 

 AS id,
  hsp.item_Name itemName,
       (SELECT SUM(Outp_Count) OutpCount
       from ods.ods_WORK_LOAD t force index(WORK_LOAD_sumindex) 
      WHERE t.visit_date >= #{startDate}
        AND t.visit_date  < #{endDate}
        AND hospital=hsp.id 

        ) this_OutpCounts
        ,
      (SELECT SUM(Outp_drug) OutpDrug   from ods.ods_HOSPITAL_INCOME t force index(HOSPITAL_INCOME_sumindex) 
      WHERE t.visit_date >= #{startDate}
        AND  t.visit_date  < #{endDate}
        AND hospital=hsp.id 

	) this_OutpDrugs,
    (SELECT SUM(Outp_Count) OutpCount
       from ods.ods_WORK_LOAD t force index(WORK_LOAD_sumindex) 
      WHERE t.visit_date >= CONCAT(cast(DATE_FORMAT(#{startDate}, '%Y')-1 as CHAR),DATE_FORMAT(#{startDate}, '-%m-%d'))
        AND t.visit_date  < CONCAT(DATE_FORMAT(#{endDate},'%Y')-1 ,DATE_FORMAT(#{endDate},'-%m-%d'),' 23:59:59.0')
        AND hospital=hsp.id 

        ) last_OutpCounts
        ,
      (SELECT SUM(Outp_drug) OutpDrug   from ods.ods_HOSPITAL_INCOME t force index(HOSPITAL_INCOME_sumindex) 
      WHERE t.visit_date >= CONCAT(cast(DATE_FORMAT(#{startDate}, '%Y')-1 as CHAR),DATE_FORMAT(#{startDate}, '-%m-%d'))
        AND t.visit_date  < CONCAT(DATE_FORMAT(#{endDate},'%Y')-1 ,DATE_FORMAT(#{endDate},'-%m-%d'),' 23:59:59.0')
        AND hospital=hsp.id 

	) last_OutpDrugs
FROM hsp.hsp_config_baseinfo hsp  
  WHERE hsp.id in 
 ) a

		select '000' id,"平均值" itemName,
		IFNULL(ROUND(AVG(t.countNew),2),0)countNew,
		IFNULL(ROUND(AVG(t.countOld),2),0)countOld 
		from(
			SELECT id,itemname,ROUND(CASE WHEN IFNULL(this_OutpCounts,0)=0 THEN 0 ELSE this_Outps/this_OutpCounts END ,2) countNew,
			ROUND(CASE WHEN IFNULL(last_OutpCounts,0)=0 THEN 0 ELSE last_Outps/last_OutpCounts END ,2) countOld
			FROM(SELECT hsp.id AS id, hsp.item_Name itemName,
		               (SELECT SUM(Outp_Count) OutpCount
						from ods.ods_WORK_LOAD t force index(WORK_LOAD_sumindex) 
		      			WHERE t.visit_date >= #{startDate}
		        		AND t.visit_date  < #{endDate}
		        		AND hospital=hsp.id 
				        ) this_OutpCounts,
				       (SELECT SUM(Outp_drug) Outp
				        from ods.ods_HOSPITAL_INCOME t force index(HOSPITAL_INCOME_sumindex) 
				        WHERE t.visit_date >= #{startDate}
				        AND  t.visit_date  < #{endDate}
				        and t.dept_code !='CW001' and t.dept_code !='tj001'
				        AND hospital=hsp.id
				       ) this_Outps,
				      (SELECT SUM(Outp_Count) OutpCount
				       from ods.ods_WORK_LOAD t force index(WORK_LOAD_sumindex) 
				       WHERE t.visit_date >= CONCAT(cast(DATE_FORMAT(#{startDate}, '%Y')-1 as CHAR),DATE_FORMAT(#{startDate}, '-%m-%d'))
				       AND t.visit_date  < CONCAT(DATE_FORMAT(#{endDate},'%Y')-1 ,DATE_FORMAT(#{endDate},'-%m-%d'),' 23:59:59.0')
				       AND hospital=hsp.id
				      ) last_OutpCounts,
				      (SELECT SUM(Outp_drug) Outp   from ods.ods_HOSPITAL_INCOME t force index(HOSPITAL_INCOME_sumindex) 
				      WHERE t.visit_date >= CONCAT(cast(DATE_FORMAT(#{startDate}, '%Y')-1 as CHAR),DATE_FORMAT(#{startDate}, '-%m-%d'))
				      AND t.visit_date  < CONCAT(DATE_FORMAT(#{endDate},'%Y')-1 ,DATE_FORMAT(#{endDate},'-%m-%d'),' 23:59:59.0')
				      and t.dept_code !='CW001' and t.dept_code !='tj001'
				      AND hospital=hsp.id 
					 ) last_Outps
				FROM hsp.hsp_config_baseinfo hsp  
				where LENGTH(hsp.id)=10
				and  hsp.comm_config_unitgrade_id=(select comm_config_unitgrade_id from hsp.hsp_config_baseinfo 
				where item_Name=#{hspName})
		 ) a
		) t

union all

SELECT id,itemname,ROUND(CASE WHEN IFNULL(this_OutpCounts,0)=0 THEN 0 ELSE this_OutpDrugs/this_OutpCounts END ,2) countNew,
	ROUND(CASE WHEN IFNULL(last_OutpCounts,0)=0 THEN 0 ELSE last_OutpDrugs/last_OutpCounts END ,2) countOld
	FROM(SELECT hsp.id AS id, hsp.item_Name itemName,
               (SELECT SUM(Outp_Count) OutpCount
				from ods.ods_WORK_LOAD t force index(WORK_LOAD_sumindex) 
      			WHERE t.visit_date >= #{startDate}
        		AND t.visit_date  < #{endDate}
        		AND hospital=hsp.id 
		        ) this_OutpCounts,
		       (SELECT SUM(Outp_drug) OutpDrug
		        from ods.ods_HOSPITAL_INCOME t force index(HOSPITAL_INCOME_sumindex) 
		        WHERE t.visit_date >= #{startDate}
		        AND  t.visit_date  < #{endDate}
		        and t.dept_code !='CW001' and t.dept_code !='tj001'
		        AND hospital=hsp.id
		       ) this_OutpDrugs,
		      (SELECT SUM(Outp_Count) OutpCount
		       from ods.ods_WORK_LOAD t force index(WORK_LOAD_sumindex) 
		       WHERE t.visit_date >= CONCAT(cast(DATE_FORMAT(#{startDate}, '%Y')-1 as CHAR),DATE_FORMAT(#{startDate}, '-%m-%d'))
		       AND t.visit_date  < CONCAT(DATE_FORMAT(#{endDate},'%Y')-1 ,DATE_FORMAT(#{endDate},'-%m-%d'),' 23:59:59.0')
		       AND hospital=hsp.id
		      ) last_OutpCounts,
		      (SELECT SUM(Outp_drug) OutpDrug   from ods.ods_HOSPITAL_INCOME t force index(HOSPITAL_INCOME_sumindex) 
		      WHERE t.visit_date >= CONCAT(cast(DATE_FORMAT(#{startDate}, '%Y')-1 as CHAR),DATE_FORMAT(#{startDate}, '-%m-%d'))
		      AND t.visit_date  < CONCAT(DATE_FORMAT(#{endDate},'%Y')-1 ,DATE_FORMAT(#{endDate},'-%m-%d'),' 23:59:59.0')
		      and t.dept_code !='CW001' and t.dept_code !='tj001'
		      AND hospital=hsp.id 
			 ) last_OutpDrugs
		FROM hsp.hsp_config_baseinfo hsp  
		where LENGTH(hsp.id)=10
		and  hsp.comm_config_unitgrade_id=(select comm_config_unitgrade_id from hsp.hsp_config_baseinfo 
		where item_Name=#{hspName})
 ) a
