mapper.select:
select t1.item_code itemCode,t1.item_name itemName,sum(ifnull(t2.cnt,0)) count
from hsp.hsp_config_baseinfo t1
left join
(select t.hospital hospital, sum(inp)/10000 cnt
    from ods.ods_HOSPITAL_INCOME t force index(HOSPITAL_INCOME_sumindex)
    where t.hospital in 
	and t.visit_date >= #{startDate}
	and t.visit_date< #{endDate} 
	group by t.hospital
) t2
on t1.item_code = t2.hospital
where length(t1.item_code)=10
and t1.id in 
and t2.cnt>0
group by t1.item_code
order by count desc;

		
	 select t1.itemCode itemCode,ifnull(t1.itemName,t1.itemCode) itemName,
        round(IFNULL(t2.cnt,0),2) count
     from
		 (
		 	select concat(t.hospital,'-',t.dept_code) itemCode,sum(inp)/10000 cnt
		 	from ods.ods_HOSPITAL_INCOME t force index(HOSPITAL_INCOME_sumindex) 
		 	where t.hospital= #{hspId}
			and t.visit_date >= #{startDate}
			and t.visit_date< #{endDate} 
		 	group by concat(t.hospital,'-',t.dept_code)
		 ) t2
		 left join
		 (
		 	select concat(b.hospital,'-',b.dept_code) itemCode,b.dept_name itemName
		 	from ods.dept_dict b
		 	where b.hospital=#{hspId}
		 ) t1
		 on t1.itemCode=t2.itemCode
		 where t2.cnt!=0
		 order by ifnull(t2.cnt,0) desc

			select itemName as itemCode,itemName as itemName,ifNull(cnt,0) count
		from (
			select concat(b.item_name,'-药费') itemName,round(sum(Inp_drug)/10000,2) cnt from ods.ods_hospital_income a force index(hospital_income_sumindex), hsp.hsp_config_baseinfo b
			where a.hospital = b.id
			and a.hospital in 
			and a.visit_date >= #{startDate}
			and a.visit_date< #{endDate}
			group by hospital
			union
			select concat(b.item_name,'-材料费') itemName ,round(sum(Inp_material)/10000,2) cnt from ods.ods_hospital_income a force index(hospital_income_sumindex), hsp.hsp_config_baseinfo b
			where a.hospital = b.id
			and a.hospital in 
			and a.visit_date >= #{startDate}
			and a.visit_date< #{endDate}
			group by hospital
			union
			select concat(b.item_name,'-其他费') itemName ,round((ifNull(sum(Inp),0)-(ifNull(sum(Inp_drug),0)+ifNull(sum(Inp_material),0)))/10000,2) cnt from ods.ods_hospital_income a force index(hospital_income_sumindex), hsp.hsp_config_baseinfo b
			where a.hospital = b.id
			and a.hospital in 
			and a.visit_date >= #{startDate}
			and a.visit_date< #{endDate}
			group by hospital
			) c
			order by c.itemName

select '000' id,"平均值" itemName, IFNULL(ROUND(AVG(t.countNew),2),0)countNew, IFNULL(ROUND(AVG(t.countOld),2),0)countOld from( select hsp.id,hsp.item_Name itemName, (select round(IFNULL(SUM(inp),0)/10000,2) thisYear from ods.ods_HOSPITAL_INCOME t force index(HOSPITAL_INCOME_sumindex) where t.visit_date>= #{startDate} and t.visit_date < #{endDate} and hospital=hsp.id) countNew, (select round(IFNULL(SUM(inp),0)/10000,2) from ods.ods_HOSPITAL_INCOME t force index(HOSPITAL_INCOME_sumindex) where t.visit_date>= concat(date_format(#{startDate},'%Y')- 1,date_format(#{startDate},'-%m-%d')) and t.visit_date < concat(date_format(#{endDate},'%Y') - 1 ,date_format(#{endDate},'-%m-%d'),' 23:59:59.0') and hospital=hsp.id) countOld from hsp.hsp_config_baseinfo hsp where LENGTH(hsp.id)=10 and hsp.comm_config_unitgrade_id=(select comm_config_unitgrade_id from hsp.hsp_config_baseinfo where item_Name=#{hspName}) ) t
union all
select hsp.id,hsp.item_Name itemName, (select round(IFNULL(SUM(inp),0)/10000,2) thisYear from ods.ods_HOSPITAL_INCOME t force index(HOSPITAL_INCOME_sumindex) where t.visit_date>= #{startDate} and t.visit_date < #{endDate} and hospital=hsp.id) countNew, (select round(IFNULL(SUM(inp),0)/10000,2) from ods.ods_HOSPITAL_INCOME t force index(HOSPITAL_INCOME_sumindex) where t.visit_date>= concat(date_format(#{startDate},'%Y')- 1,date_format(#{startDate},'-%m-%d')) and t.visit_date < concat(date_format(#{endDate},'%Y') - 1 ,date_format(#{endDate},'-%m-%d'),' 23:59:59.0') and hospital=hsp.id) countOld from hsp.hsp_config_baseinfo hsp where LENGTH(hsp.id)=10 and hsp.comm_config_unitgrade_id=(select comm_config_unitgrade_id from hsp.hsp_config_baseinfo where item_Name=#{hspName});
